const content = $input.first().json.message?.content || $input.first().json.text || '';

// 프론트매터 검증
const fmMatch = content.match(/^---\n([\s\S]*?)\n---\n([\s\S]*)/);
if (!fmMatch) {
  throw new Error('프론트매터를 찾을 수 없습니다. AI 응답을 확인하세요.');
}

const frontmatter = fmMatch[1];
const body = fmMatch[2].trim();

// 필수 필드 검증
const requiredFields = ['title', 'description', 'date', 'category', 'tags'];
for (const field of requiredFields) {
  if (!frontmatter.includes(field + ':')) {
    throw new Error(`프론트매터에 ${field} 필드가 없습니다.`);
  }
}

// 카테고리 검증
const validCategories = ['saju', 'gwansang', 'dream', 'fengshui', 'daily'];
const catMatch = frontmatter.match(/category:\s*["']?(\w+)["']?/);
if (catMatch && !validCategories.includes(catMatch[1])) {
  throw new Error(`잘못된 카테고리: ${catMatch[1]}`);
}

// 파일명 생성
const titleMatch = frontmatter.match(/title:\s*["'](.+?)["']/);
const dateMatch = frontmatter.match(/date:\s*["'](.+?)["']/);
const date = dateMatch ? dateMatch[1] : new Date().toISOString().split('T')[0];

// 제목에서 슬러그 생성 (한글 포함)
let slug = titleMatch ? titleMatch[1] : 'untitled';
slug = slug.toLowerCase()
  .replace(/[^a-z0-9가-힣\s-]/g, '')
  .replace(/\s+/g, '-')
  .substring(0, 50);

const filename = `${date}-${slug}.md`;

return [{
  json: {
    content: content,
    filename: filename,
    date: date
  }
}];
