const data = JSON.parse($input.first().json.stdout);
const autoCount = data.autoCount || 0;
const TARGET = 3;
const remaining = TARGET - autoCount;

if (remaining <= 0) {
  return [{ json: { shouldPost: false, date: data.date, recentList: '' } }];
}

const now = new Date();
const kstHour = (now.getUTCHours() + 9) % 24;
const END_HOUR = 23;
const remainingHours = END_HOUR - kstHour + 1;
const probability = Math.min(remaining / Math.max(remainingHours, 1), 1);
const shouldPost = Math.random() < probability;

return [{ json: { shouldPost, date: data.date, recentList: data.recentPosts.join('\n') } }];

const raw = $input.first().json;
const date = $('포스팅 결정').first().json.date;

let text = '';
try {
  const parts = raw.candidates[0].content.parts;
  for (const p of parts) {
    if (p.text) text += p.text;
  }
} catch(e) {
  text = JSON.stringify(raw).substring(0, 500);
}

const encoded = Buffer.from(text, 'utf-8').toString('base64');
return [{ json: { encoded: encoded, date: date } }];
